mapper.insert:
INSERT INTO RECORDS
(AC_NUM, BALANCE, MONEY, OTHER_NUM)
VALUES	(
		#{ac_num}
	,	(
			SELECT	A_BALANCE
			FROM	ACCOUNT
			WHERE	AC_NUM = #{ac_num}
		)
	,	#{money}*(-1)
	,	#{other_num}
	)

INSERT INTO RECORDS
(AC_NUM, BALANCE, MONEY, OTHER_NUM)
VALUES	(
		#{other_num}
	,	(
			SELECT	A_BALANCE
			FROM	ACCOUNT
			WHERE	AC_NUM = #{other_num}
		)
	,	#{money}
	,	#{other_num}
	)
